import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import "./PaymentPage.css";
import qrImage from "../assets/payment-qr.png"; // replace with your QR code image path

export default function PaymentPage() {
  const location = useLocation();
  const { amount } = location.state || { amount: 0 };

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [success, setSuccess] = useState(false);

  const handleProceed = () => {
    if (!name || !email) {
      alert("Please fill in both name and email.");
      return;
    }
    setSuccess(true);
  };

  return (
    <div className="payment-container">
      <h1 className="payment-title">Payment</h1>
      <div className="payment-amount">Amount: USD {amount.toFixed(2)}</div>

      <div className="qr-section">
        <img src={qrImage} alt="Payment QR Code" />
        <p>Scan the QR code to make the payment</p>
      </div>

      <div className="input-group">
        <label>Name</label>
        <input
          type="text"
          placeholder="Enter your full name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>

      <div className="input-group">
        <label>Email</label>
        <input
          type="email"
          placeholder="Enter your email address"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>

      <button className="proceed-btn" onClick={handleProceed}>
        Proceed
      </button>

      {success && (
        <div className="success-message">
          The Ticket Will Be Sent To your Mail ID
        </div>
      )}
    </div>
  );
}
