import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import "./PaymentPage.css";
import qrCodeImage from "../assets/payment-qr.png"; // replace with your image path

export default function PaymentPage() {
  const location = useLocation();
  const amount = location.state?.price || 0;

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  const handleProceed = () => {
    alert("The Ticket Will Be Sent To your Mail ID");
  };

  return (
    <div className="payment-container">
      <h1>Payment</h1>
      <p>Amount: <strong>USD {amount}</strong></p>
      <img src={qrCodeImage} alt="Payment QR" className="qr-code" />

      <div className="form-group">
        <label>Name</label>
        <input value={name} onChange={(e) => setName(e.target.value)} />
      </div>

      <div className="form-group">
        <label>Email</label>
        <input value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>

      <button onClick={handleProceed} className="proceed-btn">Proceed</button>
    </div>
  );
}
