import React, { useState } from "react";
import "./PaymentPage.css";
import { useLocation } from "react-router-dom";
import qrImage from "../assets/payment-qr.png"; // ✅ Import from src/assets

export default function PaymentPage() {
  const location = useLocation();
  const { amount } = location.state || { amount: 0 };

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  const handleProceed = () => {
    if (!name || !email) {
      alert("Please fill in your name and email before proceeding.");
      return;
    }
    alert("The Ticket Will Be Sent To your Mail ID");
  };

  return (
    <div className="payment-container">
      <h1 className="payment-title">Payment Page</h1>
      <p className="payment-amount">
        Amount to Pay: <strong>USD {amount}</strong>
      </p>

      <div className="qr-section">
        <img src={qrImage} alt="Payment QR" />
        <p>Scan this QR code to make the payment</p>
      </div>

      <div className="input-group">
        <label>Name:</label>
        <input
          type="text"
          placeholder="Enter your full name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>

      <div className="input-group">
        <label>Email:</label>
        <input
          type="email"
          placeholder="Enter your email address"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>

      <button className="proceed-btn" onClick={handleProceed}>
        Proceed
      </button>
    </div>
  );
}
