import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import sharedPartyImg from "../assets/images/sharedparty1.jpeg"; // new import
import "./SharedParty.css";

export default function SharedParty() {
  const navigate = useNavigate();

  const tickets = [
    { type: "Super Early Bird Ticket", price: 100 },
    { type: "Early Bird Ticket", price: 70 },
    { type: "Regular Ticket", price: 50 }
  ];

  const [quantities, setQuantities] = useState({});

  const handleQtyChange = (ticket, value) => {
    setQuantities({ ...quantities, [ticket]: Number(value) });
  };

  const handleBuyNow = (ticket) => {
    const qty = quantities[ticket.type] || 0;
    const totalPrice = ticket.price * qty;
    navigate("/payment", { state: { totalPrice, ticketType: ticket.type, quantity: qty } });
  };

  return (
    <div className="party-container">
      <h1 className="party-title">SHARED PARTY</h1>

      <div className="party-image">
        <img
          src={sharedPartyImg}  // updated src
          alt="Shared Party"
        />
      </div>

      <table className="ticket-table">
        <thead>
          <tr>
            <th>Ticket Type</th>
            <th>Price</th>
            <th>Qty</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {tickets.map((ticket, index) => (
            <tr key={index}>
              <td>{ticket.type}</td>
              <td>USD {ticket.price}.00</td>
              <td>
                <select
                  value={quantities[ticket.type] || 0}
                  onChange={(e) => handleQtyChange(ticket.type, e.target.value)}
                >
                  {[...Array(11).keys()].map((num) => (
                    <option key={num} value={num}>{num}</option>
                  ))}
                </select>
              </td>
              <td>
                <button
                  className="book-btn"
                  onClick={() => handleBuyNow(ticket)}
                >
                  Buy Now
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
