import React, { useState } from "react";
import "./SharedParty.css";

export default function SharedParty() {
  const [quantities, setQuantities] = useState({ t1: 0, t2: 0, t3: 0 });
  const [promoCode, setPromoCode] = useState("");

  const handleQtyChange = (ticket, value) => {
    setQuantities({ ...quantities, [ticket]: value });
  };

  return (
    <div className="party-container">
      <h1 className="party-title">SHARED PARTY</h1>

      <div className="party-image">image</div>

      <table className="ticket-table">
        <thead>
          <tr>
            <th>Ticket Type</th>
            <th>Price</th>
            <th>Qty</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Super Early Bird Ticket</td>
            <td>USD 100.00</td>
            <td>
              <select
                value={quantities.t1}
                onChange={(e) => handleQtyChange("t1", e.target.value)}
              >
                {[...Array(11).keys()].map((num) => (
                  <option key={num} value={num}>
                    {num}
                  </option>
                ))}
              </select>
            </td>
          </tr>
          <tr>
            <td>Early Bird Ticket</td>
            <td>USD 70.00</td>
            <td>
              <select
                value={quantities.t2}
                onChange={(e) => handleQtyChange("t2", e.target.value)}
              >
                {[...Array(11).keys()].map((num) => (
                  <option key={num} value={num}>
                    {num}
                  </option>
                ))}
              </select>
            </td>
          </tr>
          <tr>
            <td>Regular Ticket</td>
            <td>USD 50.00</td>
            <td>
              <select
                value={quantities.t3}
                onChange={(e) => handleQtyChange("t3", e.target.value)}
              >
                {[...Array(11).keys()].map((num) => (
                  <option key={num} value={num}>
                    {num}
                  </option>
                ))}
              </select>
            </td>
          </tr>
        </tbody>
      </table>

      <div className="promo-section">
        <label>Promotional Code</label>
        <input
          type="text"
          value={promoCode}
          onChange={(e) => setPromoCode(e.target.value)}
        />
      </div>

      <button className="book-btn">Book Now</button>
    </div>
  );
}
